import React, { Component } from 'react';
import SuggestedTasksCardView from "./SuggestedTasksCardView";
import Grid from '@material-ui/core/Grid';
import { makeStyles } from '@material-ui/core/styles';
//import Button from '@material-ui/core/Button';
import { styled } from '@material-ui/core/styles';

import { Row, Col } from 'antd';
import { Card } from 'antd';

import { Button } from 'antd';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    height: 140,
    width: 100,
  },
  control: {
    padding: theme.spacing(2),
  },
}));

const MoreButton = styled(Button)({

  margin: 40,
  padding: 20.

});

class SuggestedTasksView extends React.Component {

  constructor() {
    super()
    this.state = { dog: "fido", counter: 5, total: [0, 1, 2, 3, 4, 5, 6, 7, 8] };
    this.onClick = this.onClick.bind(this);
  }

  scrollToBottom = () => {
    this.messagesEnd.scrollIntoView({ behavior: "smooth" });
  }

  componentDidMount() {
    this.scrollToBottom();
  }

  componentDidUpdate() {
    this.scrollToBottom();
  }



  onClick() {
    this.setState({
      counter: this.state.counter + 1,
      total: this.state.total.concat([...new Array(5)]),

    });

  }

  render() {
    console.log(this.state.counter)
    console.log(this.state.total);
    let testing = this.state.dog;

    return (

      <div>

        <h1>Tasks</h1>


        <div style={{ display: "flex", justifyContent: "space-around" }}>

          <Row gutter={16} style={{ display: "flex", justifyContent: "space-around" }}>
            {this.state.total.map((value) =>
              <Col classname="gutter-row" key={value} style={{ display: "flex", justifyContent: "space-around" }} ><SuggestedTasksCardView /></Col>)}
          </Row>
        </div>




        <div style={{ display: "flex", justifyContent: "center", padding: "2%" }}>
          <Button type="primary" onClick={this.onClick} style={{
            backgroundColor: "#dedede",
            borderColor: "black",
            color: "black",
            width: "250 px",
            height: "50px",
            margin: "0 auto",
            padding: "40",
            display: "table - cell",
            verticalAlign: "middle",
          }}
            ghost> MORE </Button>
          <br></br>


        </div>

        <Grid>

        </Grid>
        <div style={{ float: "left", clear: "both" }}
          ref={(el) => { this.messagesEnd = el; }}>
        </div>
      </div >
    );
  }
}
const Greeting = props => <h1>{props.greeting}</h1>;


export default SuggestedTasksView;

