    this.scrollToBottom();
  }

  componentDidUpdate() {
    this.scrollToBottom();
  }

*/

  onClick() {
    this.setState({
      counter: this.state.counter + 1,
      total: this.state.total.concat([...new Array(5)]),

    });

  }

  render() {
    console.log(this.state.counter)
    console.log(this.state.total);
    let testing = this.state.dog;

    return (

      <div >
        <div className = "headerDiv"><h1 className = "cardHeader">Claimed Tasks</h1></div>

        <Row gutter={{sm: 2, lg: 48}}  justify="center">
      {this.state.total.map((value) =>
      <Col className="gutter-row" flex="" span={5}key={value}>

        <ClaimedTasksCardView />
      </Col>)}
    </Row>

    <div style={{display:'flex'}}>
                <Button className="bar-button" type="primary" style={{marginLeft:'2vmin', 
                        fontSize:'3vmin',marginLeft:'18.5vmin', marginTop:'4vmin', paddingBottom: '6vmin', paddingTop:'1', size:'large', paddingLeft:'6vmin', paddingRight: '8vmin', textAlign:'center'}}>Find more tasks </Button>
                        </div>


        </div>
    );
  }
}
export default ClaimedTasksView;
