import React, { Component } from "react";
import { Carousel } from "antd";
import { SmileOutlined } from '@ant-design/icons';
import { Button } from "antd"
import SuggestedTasksCard from "./SuggestedTasksCardView"
import { Card, Col, Row } from 'antd';

class Test extends React.Component {

    constructor(props) {
        super(props);
        this.next = this.next.bind(this);
        this.previous = this.previous.bind(this);
        this.carousel = React.createRef();
        this.state = { dog: "fido", counter: 0, counterEnd: 3, total: [0, 1, 2, 3, 4], fruits: ["Banana", "Orange", "Lemon", "Apple", "Mango", "Pear", "Grapes", "Lime", "Persimmon", "Tomato", "Cucumber", "Watermelon"] };

    }
    next() {
        this.carousel.next();
    }
    previous() {
        this.carousel.prev();
    }

    render() {
        const props = {
            dots: true,
            infinite: true,
            speed: 500,
            slidesToShow: 1,
            slidesToScroll: 1
        };
        let start = 0;
        let end = 4;
        let fruitsize = this.state.fruits.length;
        console.log(fruitsize);
        let newArray = new Array(fruitsize/4)
        return (
            <div>
                {newArray.map((value) =>
                value)}
                <Button size="small" onClick={this.previous}>Delete</Button>
                

                <Carousel ref={node => (this.carousel = node)} {...props}>
                    <div>
                        <div className="site-card-wrapper">
                            <Row gutter={16} >
                                {this.state.fruits.slice(start, end).map((value) =>
                                    <Col span={6} key={value}>
                                        <Card title="Card title" bordered={false}>
                                            {value}
                                        </Card>
                                    </Col>)}
                            </Row>
                            {start = start + 3}
                            {end = end + 3}
                        </div>
                    </div>
                    <div>
                    <div className="site-card-wrapper">
                            <Row gutter={16} >
                                {this.state.fruits.slice(start, end).map((value) =>
                                    <Col span={6} key={value}>
                                        <Card title="Card title" bordered={false}>
                                            {value}
                                        </Card>
                                    </Col>)}
                            </Row>
                            {start = start + 3}
                            {end = end + 3}
                        </div>
                    </div>
               
                 
                </Carousel>
                < Button onClick={this.next, start = start + 4, end = end + 4} H />
                <Button size="small" onClick={this.next}>Delete</Button>
            </div>
        );
    }
}
export default Test;
