import React, { Component } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';
import Container from '@material-ui/core/Container';
import { styled } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';



const useStyles = makeStyles({
    root: {
        minWidth: 275,
    },
    bullet: {
        display: 'inline-block',
        margin: '0 2px',
        transform: 'scale(0.8)',
    },
    title: {
        fontSize: 14,
    },
    pos: {
        marginBottom: 12,
    },
});

const MyButton = styled(Button)({
    background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
    border: 0,
    borderRadius: 3,
    boxShadow: '0 3px 5px 2px rgba(255, 105, 135, .3)',
    color: 'white',
    height: 40,
    padding: '0 52px',
    margin: 4,
});

const DoneButton = styled(Button)({
    background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
    border: 0,
    borderRadius: 3,
    boxShadow: '0 3px 5px 2px rgba(255, 105, 135, .3)',
    color: 'white',
    height: 48,
    padding: '0 40px',
    marginBottom: '25px',
    margin: 4,
});

const MoreButton = styled(Button)({
    background: 'linear-gradient(45deg, #585350 30%, #8D8987 90%)',
    border: 0,
    borderRadius: 3,
    boxShadow: '0 3px 5px 2px rgba(255, 105, 135, .3)',
    color: 'white',
    height: 40,
    padding: '0 60px',
    margin: 4,
});

class YourRequestsCardView extends Component {

    constructor(props) {
        super(props);
        this.state = { clicked: true };
        this.onClick = this.onClick.bind(this);
        this.handleClick.bind(this);
    }

    handleClick() {
        this.setState(previousState => {
            return {
                succeed: !previousState.succeed
            };
        });
    }

    onClick() {
        this.setState({
            clicked: false
        });
    }

    render() {
        const Results = () => (
            <Grid container justify="center">
                <DoneButton size="small" onClick={this.onClick} >Done!</DoneButton>
            </Grid>
        )
        const succeed = (
            <div>(253) 310-3409<br></br></div>
        )
        const notsucceed = (
            <div>
                <MoreButton size="small">
                    More ...
        </MoreButton>
                <MyButton size="small" onClick={this.onClick} >
                    Give up ...
        </MyButton>
            </div>
        )

        if (this.state.clicked) {
            return (
                <Box component="div" boxShadow={3} display="inline" m={1}>
                    <Container maxWidth="xs">
                        <Card className={useStyles.root} variant="outlined">
                            <CardContent>
                                <Typography variant="h5" color="textPrimary" gutterBottom>
                                    Getting minecraft for grandma<br />
                                </Typography>
                                <Typography variant="body2" component="p">
                                    "Lorem ipsum dolor sit amet, consectetur adipiscing elit,
                                    sed do eiusmod tempor incididunt ut labore et dolore magna
                                    aliqua. Ut enim ad minim veniam, quis nostrud exercitation
          <br />
                                    <br />
                                </Typography>
                                <Typography variant="subtitle2" component="h6">
                                    Claimed <b>5 days ago</b>
                                </Typography>
                            </CardContent>
                            <CardActions>
                                <Grid
                                    container
                                    direction="row"
                                    justify="space-between"
                                    alignItems="center"
                                >
                                    <div onClick={this.handleClick.bind(this)}>
                                        {this.state.succeed ? succeed : notsucceed}
                                    </div>


                                </Grid>

                            </CardActions>

                            {this.state.succeed ? null : <Results />}
                        </Card>
                    </Container>
                </Box>
            );
        }
        else {
            return null;
        }
    }

}

export default YourRequestsCardView;
