import React, { Component } from 'react';
import ClaimedTasksCardView from "./ClaimedTasksCardView";
import Grid from '@material-ui/core/Grid';
import { makeStyles } from '@material-ui/core/styles';
//import Button from '@material-ui/core/Button';
import { styled } from '@material-ui/core/styles';

import { Row, Col } from 'antd';
import { Card } from 'antd';

import { Button } from 'antd';

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
    },
    paper: {
        height: 140,
        width: 100,
    },
    control: {
        padding: theme.spacing(2),
    },
}));

const MoreButton = styled(Button)({

    margin: 40,
    padding: 20.

});

const style = { background: '#0092ff', padding: '8px 0' };

class ClaimedTasksView extends React.Component {

    constructor() {
        super()
        this.state = { dog: "fido", counter: 5, total: [0, 1, 2, 3] };
        this.onClick = this.onClick.bind(this);
    }

    /*  scrollToBottom = () => {
        this.messagesEnd.scrollIntoView({ behavior: "smooth" });
      }
    
      componentDidMount() {
        this.scrollToBottom();
      }
    
      componentDidUpdate() {
        this.scrollToBottom();
      }
    
    */

    onClick() {
        this.setState({
            counter: this.state.counter + 1,
            total: this.state.total.concat([...new Array(5)]),

        });

    }

    render() {
        console.log(this.state.counter)
        console.log(this.state.total);
        let testing = this.state.dog;

        return (

            <div >
                <div className="headerDiv"><h1 className="cardHeader">Claimed Tasks</h1></div>

                <Row gutter={{ sm: 2, lg: 48 }} justify="center">
                    {this.state.total.map((value) =>
                        <Col className="gutter-row" flex="" span={5} key={value}>

                            <ClaimedTasksCardView />
                        </Col>)}
                </Row>

                <div style={{ display: 'flex' }}>
                    <Button className="bar-button" type="primary"
                        style={{
                            marginLeft: '2vmin',
                            fontSize: '3vmin',
                            marginLeft: '18.5vmin',
                            marginTop: '4vmin',
                            paddingBottom: '6vmin',
                            paddingTop: '1',
                            size: 'large',
                            paddingLeft: '6vmin',
                            paddingRight: '8vmin',
                            textAlign: 'center'
                        }}
                        a rel="noopener noreferrer" href="/SuggestedTasks">Find more tasks </Button>
                </div>


            </div>
        );
    }
}
export default ClaimedTasksView;
