import React, { Component } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Cards from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Container from '@material-ui/core/Container';
import { styled } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';

import { Card } from 'antd';
import { Typography } from 'antd';
import s from '../../App.css'
import { white } from 'material-ui/styles/colors';
const { Title } = Typography;



const useStyles = makeStyles({
    root: {
        minWidth: 275,
    },
    bullet: {
        display: 'inline-block',
        margin: '0 2px',
        transform: 'scale(0.8)',
    },
    title: {
        fontSize: 14,
    },
    pos: {
        marginBottom: 12,
    },
});

const MyButton = styled(Button)({
    background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
    color: 'white',
    padding: '2% 10%'
});

const DoneButton = styled(Button)({
    background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
    color: 'white',
    padding: '2% 10%'
});

const MoreButton = styled(Button)({
    background: 'linear-gradient(45deg, #585350 30%, #8D8987 90%)',
    color: 'white',
    padding: '2% 10%'
});

class ClaimedTasksCardView extends Component {

    constructor(props) {
        super(props);
        this.state = { clicked: true };
        this.onClick = this.onClick.bind(this);
        this.handleClick.bind(this);
    }

    handleClick() {
        this.setState(previousState => {
            return {
                succeed: !previousState.succeed
            };
        });
    }

    onClick() {
        this.setState({
            clicked: false
        });
    }

    render() {
        const Results = () => (
            <Grid container justify="center">
                <DoneButton size="small" onClick={this.onClick} >Done!</DoneButton>
            </Grid>
        )
        const succeed = (
            <div>(253) 310-3409<br></br></div>
        )
        const notsucceed = (
            <div style={{justifyContent: 'space-between'}}>
                <MoreButton size="small">
                    More ...
        </MoreButton>
                <MyButton size="small" onClick={this.onClick} >
                    Give up ...
        </MyButton>
            </div>
        )

        if (this.state.clicked) {
            return (
                <div>
                    <Card className="cardStyle" style={{ width: "36vmin", height: "34vh", boxShadow: "0 8px 6px -6px #aaaaaa", margin: "5%", lineHeight: "150%" }}>
                        <h5 className="cardTitle">Getting groceries for gramma</h5>
                        <p cclassName="cardDescription"><br />Lorem ipsum dolor sit amet, consectetur adipis0ing elit, sed do eiusmod tempor
                            incididunt ut labore </p>
                        <p className="cardRequester">Requester's name</p>
                        <p className="cardRequested">Requested 10 days ago</p>
                        <div>
                        <div onClick={this.handleClick.bind(this)}>
                    {this.state.succeed ? succeed : notsucceed}
                </div>
                        </div>
                    </Card>
                </div>
            );
        }
        else {
            return null;
        }
    }
}

export default ClaimedTasksCardView;


